import React, { useState } from "react";
import API_BASE_URL from "../api";

function BlogForm({ author }) {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`${API_BASE_URL}/api/blogs`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ title, content, author }),
      });
      if (res.ok) {
        alert("✅ Blog posted successfully!");
        setTitle("");
        setContent("");
      } else {
        alert("❌ Failed to post blog. Check console for details.");
      }
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <form className="blog-form" onSubmit={handleSubmit}>
      <input value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Enter Title" required />
      <textarea value={content} onChange={(e) => setContent(e.target.value)} placeholder="Write your blog..." required />
      <button type="submit">Post Blog</button>
    </form>
  );
}

export default BlogForm;
