import React, { useState } from "react";
import API_BASE_URL from "../api"; // make sure this file exists!

function BlogForm({ author }) {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`${API_BASE_URL}/api/blogs`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          title,
          content,
          author: author || "Anonymous",
        }),
      });

      // Check if request was successful
      if (!response.ok) {
        const errorText = await response.text();
        console.error("‚ùå Server responded with:", response.status, errorText);
        alert("‚ùå Failed to post blog. Check console for details.");
        return;
      }

      // Success
      alert("‚úÖ Blog posted successfully!");
      setTitle("");
      setContent("");
    } catch (err) {
      console.error("üö® Network or CORS error:", err);
      alert("‚ùå Failed to post blog. Backend might be down or CORS blocked.");
    }
  };

  return (
    <form className="blog-form" onSubmit={handleSubmit}>
      <input
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Enter Title"
        required
      />
      <textarea
        value={content}
        onChange={(e) => setContent(e.target.value)}
        placeholder="Write your blog..."
        required
      />
      <button type="submit">Post Blog</button>
    </form>
  );
}

export default BlogForm;
